mapper.select:


    delete_time is null
	and dept_id = #{deptId}
	and co_space_id = #{coSpaceId}

order by create_time desc
 limit 1

mapper.update:
update busi_records
conference_number = #{conferenceNumber}, co_space_id = #{coSpaceId}, file_name = #{fileName}, real_name = #{realName}, template_name = #{templateName}, dept_id = #{deptId}, create_time = #{createTime}, update_time = #{updateTime}, file_size = #{fileSize}, delete_time = #{deleteTime}, record_file_status = #{recordsFileStatus},
where id = #{id}

select.where:
delete_time is not null
 and dept_id = #{deptId}
 and (conference_number like concat('%', #{searchKey}, '%') or template_name like concat('%', #{searchKey}, '%'))

mapper.update:
update busi_records
update_time = current_timestamp, delete_time = null, record_file_status = 2
where id = #{id}

mapper.select:


    delete_time is null
    and dept_id = #{deptId}
    and co_space_id = #{coSpaceId}
    and exists(select 1 from busi_history_conference bhc where bhc.co_space = busi_records.co_space_id and bhc.create_user_id = #{userId})

order by create_time desc
 limit 1



    delete_time is null
    and dept_id = #{deptId}
    and co_space_id = #{coSpaceId}
    and exists(select 1 from busi_history_participant bhp where bhp.co_space = busi_records.co_space_id and bhp.terminal_id = #{terminalId})

order by create_time desc
 limit 1
