select ca.*,tc.business_field_type,tc.business_properties,tc.name as conference_name,tc.conference_number,tc.create_user_id,tc.create_user_name,tc.tenant_id from view_conference_appointment
ca left join view_template_conference tc on ca.mcu_type = tc.mcu_type and ca.template_id = tc.id

     and ca.dept_id = #{deptId}
     and tc.name like  concat('%',#{searchKey},'%') or tc.conference_number=#{searchKey}

select ca.*,tc.business_field_type,tc.business_properties,tc.name as conference_name,tc.conference_number,tc.create_user_id,tc.create_user_name,tc.tenant_id from view_conference_appointment
ca left join view_template_conference tc on ca.mcu_type = tc.mcu_type and ca.template_id = tc.id

     and ca.dept_id = #{deptId}
     and ca.template_id = #{templateId}
     and ca.is_auto_create_template = #{isAutoCreateTemplate}
     and ca.start_time = #{startTime}
     and ca.end_time = #{endTime}
     and ca.repeat_rate = #{repeatRate}
     and ca.status = #{status}
     and ca.extend_minutes = #{extendMinutes}
     and ca.repeat_date = #{repeatDate}
     and ca.is_hang_up = #{isHangUp}
     and ca.password = #{password}
     and ca.attendee_limit = #{attendeeLimit}
     and ca.is_start = #{isStart}

        and ca.mcu_type in

            #{item}
        )

     and tc.business_field_type = #{params.businessFieldType}






                and tc.business_properties -> '$.subjectId' = #{params.businessProperties.subjectId}




                and tc.id in (
                select vtp.template_conference_id from view_template_participant vtp where vtp.mcu_type = ca.mcu_type and vtp.business_properties -> '$.classId' = #{params.businessProperties.classId}
                )





            and tc.create_user_id = #{params.businessProperties.createUserId}




            and tc.id in (
            select vtp.template_conference_id from view_template_participant vtp where vtp.mcu_type = ca.mcu_type and vtp.terminal_id = #{params.businessProperties.terminalId}
            )


     and create_by = #{createBy}
     and ca.approval_id = #{approvalId}
     and ca.room_book_id = #{roomBookId}
     and ca.type IN (0, 1, 2, 3)

